
public class TPC02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, c;
		a=1;
		b=2;
		c=a+b;
		System.out.println(c);
		
		float f; //float라고 하면 기본적으로 double로 저장됨.
		f=34.5f; // float로 저장하기 위해선 마지막 f 붙여야.
		System.out.println(f);
		
		char d;
		d='A'; // char은 '', string은 ""
		System.out.println(d);
		
		boolean g;
		g=true; // false, true
		
		System.out.println(g);
		
		// Book bk; > 이건 TPC03에서

	}

}
